mapper.select:
SELECT COUNT(*)
FROM COMMENT_BOARD
WHERE ARTICLENO = #{articleNo}

SELECT COUNT(*)
FROM COMMENT_BOARD
WHERE PARENT = #{cno}

SELECT *
FROM(
    SELECT ROW_NUMBER() OVER(ORDER BY CNO) NUM , CNO, PROFILEIMAGE, NAME, ID, CONTENT, C.REGDATE, PUB
    FROM COMMENT_BOARD C
             JOIN MEMBER M ON C.WRITER = M.ID
    WHERE PARENT = #{cno}
    )
WHERE NUM BETWEEN #{start} AND #{end}
